import axios from "axios";
import { API_SIAPSAT_GET } from "../config/api";

export const getSiapsatBinsatRenlakgiatRequest = async () => {
    // const user = getLocalUser();
    try {
        const response = await axios.get(`${API_SIAPSAT_GET}/binsat/renlakgiat`, {
            headers: {
                Authorization: `bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..NmhGNPK-EpJfPUZMxlhl8JUiB2u4_y9K5ozPiM3uxl4`,
            },
        });
        return response.data.list_data.binsiapsat_binsat_renlakgiat;
    } catch (error) {
        console.error("Error fetching items:", error);
    }
};

export const createSiapsatBinsatRenlakgiatRequest = async ({ body = {} }) => {
    // const user = getLocalUser();
    try {
        const response = await axios.post(`${API_SIAPSAT_GET}/binsat/renlakgiat`, body, {
            headers: {
                Authorization: `bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..NmhGNPK-EpJfPUZMxlhl8JUiB2u4_y9K5ozPiM3uxl4`,
                "Content-Type": "multipart/form-data",
            },
        });
        return response.data.list_data.binsiapsat_binsat_renlakgiat;
    } catch (error) {
        return error?.response?.data?.list_data ?? undefined;
    }
};

export const deleteSiapsatBinsatRenlakgiatRequest = async ({ id = null }) => {
    // const user = getLocalUser();
    try {
        const response = await axios.delete(`${API_SIAPSAT_GET}/binsat/renlakgiat/${id}`, {
            headers: {
                Authorization: `bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..NmhGNPK-EpJfPUZMxlhl8JUiB2u4_y9K5ozPiM3uxl4`,
            },
        });
        return response.data.list_data.binsiapsat_binsat_renlakgiat;
    } catch (error) {
        return error?.response?.data?.list_data ?? undefined;
    }
};